mapper.select:
SELECT *
FROM LGC_Record
WHERE Deleted = 0

        AND SerialNo in

            #{listItem}




        AND purchased = #{purchased,jdbcType=VARCHAR}

SELECT *
FROM LGC_Record
WHERE Deleted = 0

    AND SerialNo =#{serialNo}


    AND purchased = #{purchased}

mapper.update:
update LGC_Record
set SerialNo = #{serialNo}
 WHERE Deleted = 0

    AND ID in

        #{listItem}
